/*SCROLL MENU*/  
$(document).scroll(function() {    
  var scroll = $(this).scrollTop();
  if (scroll >= 500) {
      $(".header-scroll").addClass("active");
  } else {
      $(".header-scroll").removeClass("active");
  }
});

/*COLLAPSE */
$('.collapse').on('click',function(e){
  e.preventDefault();
  $(this).toggleClass('active');
});
$('.ver-mas-areas-academicas').on('click',function(e){
  e.preventDefault();
  $(this).toggleClass('active');
});
$('.collapse-home').on('click',function(e){
  e.preventDefault();
  $(this).toggleClass('active');
});
/*COLLAPSE CHANGE BUTTON*/
$(".collapse").click(function(){
  $(this).find('.up').toggleClass("hide");  
  $(this).find('.down').toggleClass("hide"); 
  $(this).find('.ver-mas-areas-academicas').toggleClass("hide");  
});
$(".collapse-home").click(function(){
  $(this).find('.up-home').toggleClass("hide");  
  $(this).find('.down-home').toggleClass("hide"); 
});


/*MODAL FORMULARIO*/
$('.btn-form-prginh').click(function(){
    $('.modal-bg').fadeIn();
    $('#modal').fadeIn();
});
$('#close').click(function(){
    $('.modal-bg').fadeOut();   
    $('#modal').fadeOut();
  return false;
});

